mapper.select:
 SELECT SQL_CALC_FOUND_ROWS
   *
 FROM
    ems.order
 WHERE
		print_flag = 0

   AND user_no = #{input.userNo}


   AND order_date BETWEEN #{input.startDate} AND DATE_ADD(#{input.endDate}, INTERVAL 1 DAY)


   AND sender = #{input.sender}


   AND regi_no = #{input.regiNo}

 order by order_date desc

   LIMIT #{input.start}, #{input.length}

SELECT SQL_CALC_FOUND_ROWS
  *
FROM
   ems.order
WHERE
	print_flag = 1

  AND user_no = #{input.userNo}


  AND order_date BETWEEN #{input.startDate} AND DATE_ADD(#{input.endDate}, INTERVAL 1 DAY)


  AND sender = #{input.sender}


  AND regi_no = #{input.regiNo}

order by order_date desc

  LIMIT #{input.start}, #{input.length}

